from . import BasicType

class InputMediaPhoto(BasicType):
    fields = {
        'type': str,
        'media': str,
        'caption': str
    }

    def __init__(self, obj=None):
        super(InputMediaPhoto, self).__init__(obj)

    @classmethod
    def a(cls, type: str, media: str, caption: str):
        return super().a(**locals())

